const initialState = {
    filter: ""
}

export const filters = (state = initialState, action) => {
    switch (action.type) {
        case "TODO_FILTER":
            return Object.assign({}, state, {
                filter: "todo"
            });

        case "COMPLETE_TASK":
            return Object.assign({}, state, {
                filter: "completeTask"
            });
          case "CLEAN_FILTER":
            return Object.assign({}, state, {
                filter: ""
            });
        default:
            return state;
    }
};
