import React from "react";
import { connect } from "react-redux";
import { doneFilter, todoFilter, cleanFilter } from "../../store";

function Filters(props) {
    function handleFilter(filter) {
        const { dispatch } = props;
        dispatch(filter());
    }

    return (
        <div>
            <button onClick={()=> handleFilter(doneFilter)}>Done</button>
            <button onClick={()=> handleFilter(todoFilter)}>ToDo</button>
            <button onClick={()=> handleFilter(cleanFilter)}>Clean Filters</button>
        </div>
    )
}

export default connect()(Filters);
