import { createStore, applyMiddleware, combineReducers } from 'redux';
import thunkMiddleWare from 'redux-thunk';
import { filters } from './reducers/filters';
import { tasks } from './reducers/tasks';


//ACTIONS
export const addTask = task => dispatch => {
    return dispatch({
        type: "ADD_TASK",
        value: task
    });
};

export const removeTask = task => dispatch => {
    return dispatch({
        type: "REMOVE_TASK",
        value: task
    });
};

export const completeTask = index => dispatch => {
    return dispatch({
        type: "COMPLETE_TASK",
        value: index
    });
};

export const todoFilter = index => dispatch => {
    return dispatch({
        type: "TODO_FILTER"
    });
};

export const cleanFilter = index => dispatch => {
    return dispatch({
        type: "CLEAN_FILTER"
    });
};


export function initializeStore() { //el nombre de la funcion puede ser cualquiera. 
    return createStore(
        combineReducers({ filters, tasks }),
        {},
        applyMiddleware(thunkMiddleware)
    );
}
