export const addTask = task => dispatch => {
    return dispatch({
        type: "ADD_TASK",
        value: task
    });
};

export const removeTask = task => dispatch => {
    return dispatch({
        type: "REMOVE_TASK",
        value: task
    });
};


export const completeTask = index => dispatch => {
    return dispatch({
        type: "COMPLETE_TASK",
        value: index
    });
};
