const initialState = [];


export const tasks = (state = initialState, action) => {
    switch (action.type) {
        case "ADD_TASK":
            return [...state.tasks, {value: action.value, status: "todo"}]

        case "REMOVE_TASK":
          return state.map((item, index) => {
              if (index == action.value) {
                  item.status = "done";
              }
              return item;
          })
        default:
            return state;
    }
};
