import { createStore, applyMiddleware, combineReducers } from 'redux'; //combineReducers is a function that put tog ether all reducers
import thunkMiddleWare from 'redux-thunk';
import { filters } from './reducers/filters';
import { tasks } from './reducers/tasks';


//ACTIONS




export function initializeStore() { //el nombre de la funcion puede ser cualquiera. 
    return createStore(
        combineReducers({ filters, tasks }),
        {},
        applyMiddleware(thunkMiddleWare)
    );
}
