export const completeTask = index => dispatch => {
    return dispatch({
        type: "COMPLETE_TASK",
        value: index
    });
};

export const todoFilter = index => dispatch => {
    return dispatch({
        type: "TODO_FILTER",
        value: index
    });
};

export const cleanFilter = index => dispatch => {
    return dispatch({
        type: "CLEAN_FILTER",
        value: index
    });
};
